<script>export let disabled = false;
export let value;
function changeValue(e) {
    const target = e.target;
    const x = parseFloat(target.value);
    if (!Number.isNaN(x)) {
        value = x;
    }
    else {
        target.value = value.toString();
    }
}
</script>

<div class="flex">
	<button
		on:click={() => {
			value -= 1;
		}}
		{disabled}
		class="left"
	>
		-
	</button>
	<input
		type="number"
		inputmode="decimal"
		{value}
		{disabled}
		on:change={changeValue}
		style="width: 80px"
	/>
	<button
		on:click={() => {
			value += 1;
		}}
		{disabled}
		class="right"
	>
		+
	</button>
</div>

