<script>export let disabled = false;
export let value;
function changeValue(e, i) {
    const target = e.target;
    const x = parseFloat(target.value);
    if (!Number.isNaN(x)) {
        value[i] = x;
    }
    else {
        target.value = value[i].toString();
    }
}
function decrement(i) {
    const v = value.slice();
    v[i] -= 1;
    value = v;
}
function increment(i) {
    const v = value.slice();
    v[i] += 1;
    value = v;
}
function extend() {
    const v = value.slice();
    v.push(v.length ? v[v.length - 1] : 0);
    value = v;
}
function reduce() {
    value = value.slice(0, value.length - 1);
}
</script>

{#if value && Array.isArray(value)}
	<div class="flex items-center">
		{#each value as v, i}
			<div class="flex mr-2">
				<button {disabled} on:click={() => decrement(i)} class="left"> - </button>
				<input
					type="number"
					inputmode="decimal"
					value={v}
					on:change={(e) => changeValue(e, i)}
					{disabled}
					style="width: 80px"
				/>
				<button {disabled} on:click={() => increment(i)} class="right"> + </button>
			</div>
		{/each}
		<button on:click={() => reduce()} {disabled} class="left"> - </button>
		<button on:click={() => extend()} {disabled} class="right" style="border-left: none;">
			+
		</button>
	</div>
{/if}

