import { Stream, Sink, Scheduler, Disposable } from '@most/types';
export default class FilterMap<A, B> implements Stream<B> {
    private readonly p;
    private readonly f;
    private readonly source;
    constructor(p: (a: A) => boolean, f: (a: A) => B, source: Stream<A>);
    run(sink: Sink<B>, scheduler: Scheduler): Disposable;
}
